import styled from "styled-components";

const ViewMoreButton = styled.div`
  border: 2px solid #000;
  border-radius: 5px;
  width: 500px;
  padding: 15px 0;
  color: #494957;
  cursor: pointer;
  font-size: 15px;
  text-align: center;
  margin: 70px auto;

  :hover {
    background: #0d0d19;
    color: #fff;
  }
`;

export default ViewMoreButton;
